// src/components/SearchBar.js
import React from 'react';

export default function SearchBar({ searchTerm, onSearch }) {
  return (
    <div className="search-bar">
      <input
        type="text"
        value={searchTerm}
        onChange={e => onSearch(e.target.value)}
        placeholder="Search products..."
      />
    </div>
  );
}
